// quote.component.html
<h2>Get a Quote</h2>

// City selection dropdown
<label for="city">Select City:</label>
<select id="city" [(ngModel)]="selectedCity" (change)="onCityChange()">
  <option value="Stockholm">Stockholm</option>
  <option value="Uppsala">Uppsala</option>
</select>

// Area input field
<label for="area">Enter Area (m²):</label>
<input type="number" id="area" [(ngModel)]="area" />

<div>
  <h3>Add-Ons:</h3>
// Checkbox inputs for add-ons
  <input type="checkbox" [(ngModel)]="addOns.windowCleaning" /> Window Cleaning
  <input type="checkbox" [(ngModel)]="addOns.balconyCleaning" /> Balcony Cleaning
  <input type="checkbox" [(ngModel)]="addOns.junkRemoval" /> Junk Removal
</div>

// Button to calculate the quote
<button (click)="calculateQuote()">Get Quote</button>

<div>
  <h3>Quote Summary:</h3>
// Display area and city if they are selected
  <p *ngIf="area">Area: {{ area }} m²</p>
  <p *ngIf="selectedCity">City: {{ selectedCity }}</p>
// Display total quote if calculated
  <p *ngIf="quote !== null">Total Quote: {{ quote }} SEK</p>
</div>
// quote.component.ts
import { Component } from '@angular/core';

@Component({
  selector: 'app-quote',
  templateUrl: './quote.component.html',
})
export class QuoteComponent {
  selectedCity: string = 'Stockholm'; // Default city
  area: number | null = null; // Area input
  addOns: { [key: string]: boolean } = {
    windowCleaning: false,
    balconyCleaning: false,
    junkRemoval: false,
  };
  quote: number | null = null; // To hold calculated quote

  onCityChange() {
    // Logic for handling city change if needed
  }

  calculateQuote() {
    const pricePerSquareMeter = this.selectedCity === 'Stockholm' ? 65 : 55; // Example pricing
    let totalQuote = 0;

    if (this.area) {
      totalQuote = this.area * pricePerSquareMeter; // Calculate based on area
    }

    // Add costs for selected add-ons
    if (this.addOns.windowCleaning) {
      totalQuote += 300; // Example cost for window cleaning
    }
    if (this.addOns.balconyCleaning) {
      totalQuote += 150; // Example cost for balcony cleaning
    }
    if (this.addOns.junkRemoval) {
      totalQuote += 400; // Example cost for junk removal
    }

    this.quote = totalQuote; // Set the total quote
  }
}
